import React, { useState, useRef } from 'react';
import { Howl } from 'howler';
import { FaPlay, FaPause, FaForward, FaBackward } from 'react-icons/fa';
import './Estilos/music-player.css';

const MusicPlayer = ({ audioFiles }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTrack, setCurrentTrack] = useState(0);
  const [sound, setSound] = useState(null);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);

  const playTrack = () => {
    const newSound = new Howl({
      src: [audioFiles[currentTrack]],
      onend: () => {
        playNextTrack();
      },
    });

    newSound.play();

    setSound(newSound);
    setIsPlaying(true);
  };

  const togglePlay = () => {
    if (sound) {
      if (isPlaying) {
        sound.pause();
      } else {
        sound.play();
      }

      setIsPlaying(!isPlaying);
    }
  };

  const playPreviousTrack = () => {
    const newTrack = (currentTrack - 1 + audioFiles.length) % audioFiles.length;
    setCurrentTrack(newTrack);
    playTrack();
  };

  const playNextTrack = () => {
    const newTrack = (currentTrack + 1) % audioFiles.length;
    setCurrentTrack(newTrack);
    playTrack();
  };

  const onMouseDown = (e) => {
    const { clientX, clientY } = e;
    setPosition({ x: clientX - position.x, y: clientY - position.y });
    setDragging(true);

    document.addEventListener('mousemove', onMouseMove);
    document.addEventListener('mouseup', onMouseUp);
  };

  const onMouseMove = (e) => {
    if (dragging) {
      const { clientX, clientY } = e;
      setPosition({ x: clientX - position.x, y: clientY - position.y });
    }
  };

  const onMouseUp = () => {
    setDragging(false);

    document.removeEventListener('mousemove', onMouseMove);
    document.removeEventListener('mouseup', onMouseUp);
  };

  const style = {
    transform: `translate(${position.x}px, ${position.y}px)`,
    cursor: dragging ? 'grabbing' : 'grab',
    position: 'absolute',
    bottom: '0',
    right: '0',
  };

  return (
    <div className="music-player" style={style} onMouseDown={onMouseDown}>
      <div className="controls">
        <button onClick={playPreviousTrack}>
          <FaBackward />
        </button>
        <button onClick={togglePlay}>
          {isPlaying ? <FaPause /> : <FaPlay />}
        </button>
        <button onClick={playNextTrack}>
          <FaForward />
        </button>
      </div>
    </div>
  );
};

export default MusicPlayer;
