import React, { useState, useEffect } from 'react';
import './Estilos/slider-container.css'
import { motion } from 'framer-motion'

const ImageSlider = ({ images }) => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const [isDragging, setIsDragging] = useState(false);

    // Funci칩n para pasar a la siguiente imagen
    const nextSlide = () => {
        if (!isDragging) {
            const newIndex = (currentIndex + 1) % images.length;
            setCurrentIndex(newIndex);
        }
    };

    // Efecto secundario para cambiar de imagen autom치ticamente
    useEffect(() => {
        const interval = setInterval(nextSlide, 100); // Cambia la imagen cada 0.1 segundos (100 milisegundos)

        // Limpia el intervalo cuando el componente se desmonta
        return () => clearInterval(interval);
    }, [currentIndex, images, isDragging]);
    return (
        <motion.div 
            className='slider-container'
            onMouseDown={() => setIsDragging(true)}
            onMouseUp={() => setIsDragging(false)}
            onTouchStart={() => setIsDragging(true)}
            onTouchEnd={() => setIsDragging(false)}
        >
            <motion.div 
                className='slider' 
                drag={isDragging ? 'x' : false} 
                dragConstraints={{ right: 0 }}
            >
                {images.map((image, index) => (
                    <motion.div key={index} className={`item ${index === currentIndex ? 'active' : ''}`}>
                        <img src={image} alt="" />
                    </motion.div>
                ))}
            </motion.div>
        </motion.div>
    );
}

export default ImageSlider

