import React, { useState, useEffect } from 'react';
import './Estilos/slider-container.css'
import { motion } from 'framer-motion'

const ImageSlider = ({ images }) => {
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const slider = document.querySelector('.slider');
        const totalImages = images.length;
        const clonedImages = [...images, ...images]; // Clonar las imágenes para hacer el slider infinito

        const interval = setInterval(() => {
            setCurrentIndex(prevIndex => (prevIndex + 1) % totalImages);
        }, 3000); // Cambiar de imagen cada 3 segundos (puedes ajustar este valor)

        // Limpiar el intervalo cuando el componente se desmonta para evitar fugas de memoria
        return () => clearInterval(interval);

    }, [images]);

    return (
        <motion.div className='slider-container'>
            <motion.div className='slider' drag='x' dragConstraints={{ right: 0 }}>
                {images.map((image, index) => (
                    <motion.div className='item' key={index} style={{ width: '100%' }}>
                        <img src={image} alt="" />
                    </motion.div>
                ))}
            </motion.div>
        </motion.div>
    );
}

export default ImageSlider

