import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './Estilos/slider-container.css';
import YTSearch from 'youtube-api-search';

const ImageSlider = ({ images }) => {
  return (
    <motion.div className='slider-container'>
      <motion.div className='slider' drag='x' dragConstraints={{ right: 0 }}>
        {images.map((image, index) => {
          // Verifica si la URL termina con una extensión de imagen o es una URL de YouTube
          const isImage = /\.(jpeg|jpg|gif|png)$/.test(image);
          const isYouTubeVideo = /^(https?:\/\/)?(www\.)?(youtube|youtu|youtube-nocookie)\.(com|be)\/.+$/.test(image);

          return (
            <motion.div className='item' key={index}>
              {isImage ? (
                <img src={image} alt={`image-${index}`} />
              ) : isYouTubeVideo ? (
                <iframe
                  title={`video-${index}`}
                  src={image}
                  frameBorder='0'
                  allow='autoplay; encrypted-media'
                  allowFullScreen
                />
              ) : (
                <p>Formato no compatible</p>
              )}
            </motion.div>
          );
        })}
      </motion.div>
    </motion.div>
  );
};

export default ImageSlider;





