import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './Estilos/slider-container.css';
import YTSearch from 'youtube-api-search';

const ImageSlider = ({ images, apiKey }) => {
  const [videoUrls, setVideoUrls] = useState([]);

  useEffect(() => {
    // Realiza una búsqueda en YouTube cuando el prop 'images' o 'apiKey' cambie
    const youtubeVideos = images.filter(image => image.startsWith('https://www.youtube.com/'));
    if (youtubeVideos.length > 0 && apiKey) {
      const videoIds = youtubeVideos.map(video => {
        const videoId = video.split('v=')[1];
        const ampersandPosition = videoId.indexOf('&');
        if (ampersandPosition !== -1) {
          return videoId.substring(0, ampersandPosition);
        }
        return videoId;
      });

      // Realiza la búsqueda de videos de YouTube usando la API de búsqueda
      YTSearch({ key: apiKey, term: videoIds.join(',') }, videos => {
        const videoUrls = videos.map(video => `https://www.youtube.com/embed/${video.id.videoId}`);
        setVideoUrls(videoUrls);
      });
    }
  }, [images, apiKey]);

  return (
    <motion.div className='slider-container'>
      <motion.div className='slider' drag='x' dragConstraints={{ right: 0 }}>
        {images.map((image, index) => {
          const isImage = image.endsWith('.jpeg') || image.endsWith('.jpg') || image.endsWith('.gif') || image.endsWith('.png');
          
          return (
            <motion.div className='item' key={index}>
              {isImage ? (
                <img src={image} alt={`image-${index}`} />
              ) : (
                <iframe title={`video-${index}`} src={videoUrls[index]} frameBorder='0' allow='autoplay; encrypted-media' allowFullScreen />
              )}
            </motion.div>
          );
        })}
      </motion.div>
    </motion.div>
  );
};

export default ImageSlider;




