import React, { useEffect, useState } from 'react';
import './Estilos/Toolbar.css';
import './Estilos/Toolbar-button.css';
import './Estilos/Button-style.css';
import './Estilos/Toolbar-nombre.css';
import './Estilos/Toolbar-nombre2.css';
import { Link, useHistory } from 'react-router-dom';

const Toolbar = () => {
    const [isSticky, setIsSticky] = useState(false);
    const history = useHistory();

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 0) {
                setIsSticky(true);
            } else {
                setIsSticky(false);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    useEffect(() => {
        window.addEventListener('scroll', function () {
            var toolbar = document.querySelector('.Toolbar');

            if (window.scrollY > 0) {
                toolbar.style.backgroundColor = 'rgba(0, 128, 0, 0.4)';
                toolbar.style.borderBottom = '3px solid black';
            } else {
                toolbar.style.backgroundColor = 'transparent';
                toolbar.style.borderBottom = 'none';
            }
        });
    }, []);

    const scrollToAboutMe = () => {
        // Manejar el desplazamiento hacia la sección con la id 'aboutme'
        const aboutMeSection = document.getElementById('aboutme');
        if (aboutMeSection) {
            aboutMeSection.scrollIntoView({ behavior: 'smooth' });
        }
    };

    return (
        <div className="Toolbar">
            <div className='Toolbar-nombre'>
                <h1 className='Toolbar-nombre2'>
                    Albert Formatger
                </h1>
            </div>
            <div className='Toolbar-button'>
                <button className='Button-style'>Home</button>
                <button onClick={scrollToAboutMe} className='Button-style'>About me</button>
                <button className='Button-style'>Media</button>
                <button className='Button-style'>Contact</button>
            </div>
        </div>
    );
}

export default Toolbar;
