import React, { useState } from 'react';
import YouTube from 'react-youtube';
import './Estilos/slider-container-video.css';
import { motion } from 'framer-motion';

const VideoSlider = ({ videos }) => {
    const [videoIndex, setVideoIndex] = useState(0);

    // Calcula el ancho de cada video basado en la cantidad de videos que tienes
    const videoWidth = 640; // Ancho del video
    const totalVideos = videos.length;
    const containerWidth = videoWidth * totalVideos;

    const opts = {
        height: '200',
        width: '300',
        playerVars: {
            autoplay: 1,
        },
    };

    const handleDrag = (_, info) => {
        // Calcula dinámicamente el índice del video basado en la posición actual del slider
        const newIndex = Math.round(info.point.x / videoWidth);
        // Asegúrate de que el nuevo índice esté dentro del rango de videos
        if (newIndex >= 0 && newIndex < totalVideos) {
            setVideoIndex(newIndex);
        }
    };

    return (
        <motion.div className='slider-container-video'>
            <motion.div className='slider-video' drag='x' dragConstraints={{ right: 0 }} onDrag={handleDrag} style={{ width: containerWidth }}>
                {videos.map((video, index) => (
                    <motion.div className='item-video' key={index} style={{ width: videoWidth }}>
                        {videoIndex === index && <YouTube videoId={video} opts={opts} />}
                    </motion.div>
                ))}
            </motion.div>
        </motion.div>
    );
};

export default VideoSlider;
