import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import './Estilos/App.css';
import './Estilos/Toolbar.css';
import './Estilos/Fondo.css';
import Toolbar from './Toolbar';
import About from './About';

function App() {
  // Utiliza useEffect para controlar el desplazamiento suave
  useEffect(() => {
    const handleSmoothScroll = (e) => {
      e.preventDefault(); // Evita el comportamiento predeterminado del enlace
      const targetId = e.target.getAttribute('href').substring(1); // Obtiene el ID del objetivo
      const targetElement = document.getElementById(targetId); // Encuentra el elemento objetivo
      if (targetElement) {
        window.scrollTo({
          top: targetElement.offsetTop,
          behavior: 'smooth', // Realiza el desplazamiento suave
        });
      }
    };

    // Escucha el clic en enlaces internos que contienen '#'
    document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
      anchor.addEventListener('click', handleSmoothScroll);
    });

    return () => {
      // Limpia los event listeners al desmontar el componente
      document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
        anchor.removeEventListener('click', handleSmoothScroll);
      });
    };
  }, []);

  return (
    <div>
      <div className='Fondo'>
        <Toolbar />
      </div>
      <div id="aboutme">
        <About />
      </div>
    </div>
  );
}

export default App;
