const fs = require('fs');
const path = require('path');

function obtenerFotosEnCarpeta(carpeta) {
    try {
        const archivos = fs.readdirSync('fotos');
        const fotos = archivos.filter(archivo => {
            const extension = path.extname(archivo);
            return ['.jpg', '.jpeg', '.png', '.gif'].includes(extension.toLowerCase());
        });
        const urlsFotos = fotos.map(foto => path.join('./fotos', foto));

        return urlsFotos;
    } catch (error) {
        console.error('Error al leer la carpeta:', error);
        return [];
    }
}

export default obtenerFotosEnCarpeta;
